void Screen1ViewBase::setupScreen()
{

}

void Screen1ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &AutoBtn)
    {
        //InteractionAuto
        //When AutoBtn clicked change screen to Screen2
        //Go to Screen2 with no screen transition
        application().gotoScreen2ScreenNoTransition();
    }
}

void Screen1ViewBase::sliderValueChangedCallbackHandler(const touchgfx::Slider& src, int value)
{
    if (&src == &sliderVoltage)
    {
        //InteractionSliderVoltage
        //When sliderVoltage value changed call virtual function
        //Call setVoltage
        setVoltage(value);
    }
}
